import React, { Component } from 'react'
import Chip from '@material-ui/core/Chip'

const chipStyle = {
    marginTop: '10px',
    marginRight: '5px'
}

const sectionTitle = {
    marginTop: '40px'
}


export default class FeatureChips extends Component{

    removeFeature = (feature) => {
        for(var i=0; i<this.props.features.length; i++){
            if (this.props.features == feature){
                this.setState({features: this.props.features.splice(i, 1)})
            }
        }
    }

    chipDiv = this.props.features.map(function(feature, i){
            return <Chip variant='outlined' style={chipStyle} key={i} label={feature} color='primary' onDelete={() => this.removeFeature(feature)}></Chip>
        })

    render() {
        return(
            <div>
                <h3 style={sectionTitle}>Select features</h3>
                {this.chipDiv}
            </div>
        )
    }
}
