import {React, Component} from 'react';
import { makeStyles, withStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

const styles = theme => ({
    formControl: {
      margin: theme.spacing(1),
      minWidth: 120,
    },
    selectEmpty: {
      marginTop: theme.spacing(2),
    },
});


class TargetSelector extends Component{

    constructor(props){
        super(props)
        this.state = {
            target : '',
            features : props.features
        }
    }

    handleChange(selection){
        this.setState({target: selection})
    }

    render(){
        const inputLabel = React.useRef(null);
        const [labelWidth, setLabelWidth] = React.useState(0);
        const {classes} = this.props;

        var choices = []
        for(var i=0; i<this.state.features.length; i++){
            var feature = this.state.features[i]
        choices.push(<MenuItem value={feature}>{feature}</MenuItem>)
        }
        return(
            <FormControl variant="outlined" className={this.classes.formControl}>
                <InputLabel ref={inputLabel} id="demo-simple-select-outlined-label">
                    Age
                </InputLabel>
                <Select
                    labelId="demo-simple-select-outlined-label"
                    id="demo-simple-select-outlined"
                    value={this.state.target}
                    onChange={this.handleChange.bind(this, this.state.target)}
                    labelWidth={labelWidth}
                >
                    {choices}
                </Select>
            </FormControl>
        )
    }
}

export default withStyles(styles)(TargetSelector)
