import React, { useEffect, useState, Component } from 'react';
import ProjectBuilder from './projectBuilder'
import SideMenu from './sideMenu'
import { Switch, Route, Redirect, useRouteMatch } from "react-router-dom";
import ProjectsTable from './projectsTable';

export default class Dashboard extends Component {

  constructor(props) {

    super(props)
    this.state = {
      user: props.user,
      firebase: props.firebase,
      jobData: {}
    }
  }

  componentDidMount(){
    loadData()
  }

  loadData() {
    firebase.listenForData(user, function (job, job_data) {
      var newData = this.state.jobData
      newData[job] = job_data
      this.setState({jobData: newData})
    })
  }

  render(){
    return(
      <div>
        { console.log(jobData) }
        < SideMenu
          firebase = { firebase }
        />
        <Switch>
          <Route path={`${match.path}/new_project`}>
            <ProjectBuilder user={user} firebase={firebase} />
          </Route>
          <Route path={`${match.path}/all_projects`}>
            <ProjectsTable user={user} data={jobData} firebase={firebase} />
          </Route>
          <Route path={`${match.path}/settings`}>
            <div>
              Settings page
                </div>
          </Route>
        </Switch>
      </div >
    )
  }
}
