import React, { useState } from 'react';
import ProjectBuilder from './projectBuilder'
import SideMenu from './sideMenu'
import AllProjects from './allProjects'
import { makeStyles, useTheme } from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
  toolbar: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: theme.spacing(0, 1),
    ...theme.mixins.toolbar,
  },
  content: {
    flexGrow: 1,
    padding: theme.spacing(3),
  }
}));

export default function Dashboard(props) {

  const classes = useStyles();
  const theme = useTheme();
  const user = props.user
  const firebase = props.firebase
  const newProject = <ProjectBuilder firebase={props.firebase} user={props.user} />
  const allProjects = <AllProjects user={user} data={null} firebase={firebase} />
  const [selectedPage, setSelectedPage] = useState(newProject)


  function loadData() {
    let dashboard = this;
    this.firebase.get_user_data(this.user, function (data) {
      dashboard.setState({ data: data })
    })
  }

  return (
    <div>
      <SideMenu
        newProject={newProject}
        allProjects={allProjects}
        settings={5}
        firebase={firebase}
        selectedPage={setSelectedPage}
      />
      <main className={classes.content}>
        <div className={classes.toolbar} />
        {selectedPage}
      </main>
    </div>
  );
}
