import React, { useEffect, useState } from 'react';
import ProjectBuilder from './projectBuilder'
import SideMenu from './sideMenu'
import { Switch, Route, Redirect, useRouteMatch } from "react-router-dom";
import ProjectsTable from './projectsTable';

export default function Dashboard(props) {

  const user = props.user;
  const firebase = props.firebase;
  const [dataChange, setDataChange] = useState(0)
  const [jobData, setJobData] = useState({})
  let match = useRouteMatch()
  useEffect(() => {
    setListeners()
  }, [])
  useEffect(() => {
    console.log(dataChange)
  }, [dataChange])

  const setListeners = () => {
    firebase.listenForData(user, function (job, job_data) {
      setDataChange(dataChange+1)
    })
  }

  const loadData = () => {
    // data = {}
    // firebase.loadData(user, function(snapshots){

    // })
  }

  return (
    <div>
      {console.log(jobData)}
      <SideMenu
        firebase={firebase}
      />
      <Switch>
        <Route path={`${match.path}/new_project`}>
          <ProjectBuilder user={user} firebase={firebase} />
        </Route>
        <Route path={`${match.path}/all_projects`}>
          <ProjectsTable user={user} data={jobData} firebase={firebase} />
        </Route>
        <Route path={`${match.path}/settings`}>
          <div>
            Settings page
          </div>
        </Route>
      </Switch>
    </div>
  )
}
