import React, { Component } from 'react'
import Chip from '@material-ui/core/Chip'


export default class FeatureChips extends Component{


    removeFeature(feature){
        for(var i=0; i<this.props.features.length; i++){
            if (this.props.features == feature){
                this.setState({features: this.props.features.splice(i, 1)})
            }
        }
    }

    createChips = (function(){
        var chipDiv = this.props.features.map(function(feature){
            return <Chip labe={feature} color='primary' onDelete={() => this.removeFeature(feature)}></Chip>
        })
    })

    render() {
        return(
            <div>
                <span>Features</span>
                <br></br>
                <this.createChips />
            </div>
        )
    }
}
