import React, { Component } from 'react'
import Chip from '@material-ui/core/Chip'

const chipStyle = {
    marginTop: '10px',
    marginRight: '5px'
}

const sectionTitle = {
    marginTop: '40px'
}


export default class FeatureChips extends Component{

    constructor(props){

        super(props);
        this.state = {
            features: props.features
        }
        this.deleteFeature = this.deleteFeature.bind(this);
    }

    deleteFeature(e){
        this.setState({features: this.state.features.filter((_, i) => i !== e.target.key)})
    }

    render() {
        var chips = []
        for (var i=0; i<this.state.features.length; i++){
            chips.push(<Chip variant='outlined' style={chipStyle} key={i} label={this.state.features[i]} color='primary' onDelete={this.deleteFeature}></Chip>)
        }
        return(
            <div>
                <h3 style={sectionTitle}>Select features</h3>
                {chips}
            </div>
        )
    }
}
