import React, {useEffect, useState, useReducer} from 'react';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Paper from '@material-ui/core/Paper';

const StyledTableCell = withStyles(theme => ({
  head: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  body: {
    fontSize: 14,
  },
}))(TableCell);

const StyledTableRow = withStyles(theme => ({
  root: {
    '&:nth-of-type(odd)': {
      backgroundColor: theme.palette.background.default,
    },
  },
}))(TableRow);

function createData(id, dateCreated, accuracy, size, downloadLink) {
  return { id, dateCreated, accuracy, size, downloadLink };
}

const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
];

const useStyles = makeStyles({
  table: {
    minWidth: 700,
  },
  content:{
    paddingLeft: 100,
    paddingTop: 75,
    paddingRight: 50
  }
});

export default function ProjectsTable(props) {
  const classes = useStyles();
  const firebase = props.firebase
  const user = props.user
  const [data, setData] = useState({})
  const [rows, setRows] = useReducer((files, { type, value }) => {
    switch (type) {
      case "add":
        return [...files, value];
      case "remove":
        return files.filter((_, index) => index !== value);
      default:
        return files;
    }
  }, []);

  useEffect(() => {
    getData();
  }, []);

  function addJobs(data){
    var jobs = data.jobs
    setRows({ type: "add", value: jobs})
    console.log(data)
  }

  function getData(){
    firebase.listenForData(user, function(data){
      setData(data)
      addJobs(data)
    })
  }

  return (
    <div className={classes.content}>
      <h1>
        All Projects
      </h1>
      <TableContainer component={Paper}>
