import React, { useEffect, useState, useRef } from 'react';
import { useRouteMatch, Link } from 'react-router-dom'
import ImageUploader from 'react-images-upload'
import { Button, Typography, Divider } from '@material-ui/core'
import { GetApp } from '@material-ui/icons'

export default function Project(props) {

    const match = useRouteMatch()
    const data = props.data[match.params.id]
    const [image, setImage] = useState(null)
    const [results, setResults] = useState({})

    function onDrop(uploadedImages, url) {
        setImage(uploadedImages[0])
    }

    function performPrediction() {
        setResults({})
        const url = 'http://127.0.0.1:8080/predict/' + match.params.id
        const formData = new FormData()
        formData.append('image', image)
        const options = {
            method: 'POST',
            body: formData
        }
        fetch(url, options)
            .then(response => response.json())
            .then(result => {
                setResults(result)
                console.log(result)
            })
            .catch(error => {
                console.log(error)
            })
    }

    function downloadTensorboard(){

    }

    const ShowResults = () => {
        const predictions = results['predictions']
        console.log(Object.keys(results).length)
        return (
            Object.keys(results).length > 0 &&
            <div>
                <h1>
                    Predicted {results['label_map'][predictions[0].indexOf(Math.max(...predictions[0])).toString()]} with {Math.max(...predictions[0]) * 100}% confidence
                </h1>
            </div>
        )
    }

    const ShowPredictor = () => {

        return (
            image &&
            <div>
                <Button variant='contained' color='primary' onClick={performPrediction}>Predict</Button>
            </div>
        )
    }


    return (
        <div>
            <h1>{data.name}</h1>
            <Divider></Divider>
            <Button variant='contained' color='primary' endIcon={<GetApp />}>
                {console.log(data.tb_logs)}
                <a rel="noopener noreferrer" href={data.tb_logs} target="_blank">Tensorboard logs</a>
            </Button>
            <ImageUploader
                withIcon={true}
                buttonText='Upload Image'
                onChange={onDrop}
                imgExtension={['.jpg', '.jpeg', '.png']}
                singleImage={true}
                withPreview={true}
            />
            <ShowPredictor />
            <ShowResults />
        </div>
    )
}
