import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import DropboxIcon from './dropboxIcon'


const styles = makeStyles(theme => ({
    dropboxButton:{
        marginTop: '20px'
    }
  }));

export default function Builder(){

    var scriptLoaded = false
    const classes = styles();

    function connectToDropbox(){
        // var script = document.createElement('script')
        // script.setAttribute('data-app-key', 'z5p9y3pr6ufqsbh')
        // script.src = "https://www.dropbox.com/static/api/2/dropins.js"
        // script.id = "dropboxjs"
        // script.type = "text/javascript"
        // document.head.appendChild(script);

        // console.log("clicked")
        var options = {

            // Required. Called when a user selects an item in the Chooser.
            success: function(files) {
                alert("Here's the file link: " + files[0].link)
            },
            cancel: function() {

            },
            linkType: "direct", // or "direct"
            multiselect: false, // or true
            folderselect: false, // or true
        };
        if (scriptLoaded){
            console.log("Trying to open picker")
            window.Dropbox.choose(options)
        }else{
            while (!scriptLoaded){
                //
            }
            window.Dropbox.choose(options)
        }

    }


    return (
        <div>
            <script type="text/javascript" onLoad={scriptLoaded=true} src="https://www.dropbox.com/static/api/2/dropins.js" id="dropboxjs" data-app-key="z5p9y3pr6ufqsbh"></script>
            <h1>Let's get started</h1>
            <ol>
                <li>Let's get started by uploading a dataset. Upload your data to Dropbox if you haven't already, and continue by logging in.</li>
                <Button startIcon={<DropboxIcon/>} variant='outlined' color='primary' onClick={connectToDropbox} className={classes.dropboxButton}>Connect to dropbox</Button>
            </ol>
        </div>
    )
}
