import React from 'react';
import ProjectBuilder from './projectBuilder'
import SideMenu from './sideMenu'
import AllProjects from './allProjects'
import { makeStyles, useTheme } from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
  toolbar: {
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'flex-end',
      padding: theme.spacing(0, 1),
      ...theme.mixins.toolbar,
  },
  content: {
      flexGrow: 1,
      padding: theme.spacing(3),
  }
}));

export default class Dashboard extends React.Component {

  constructor(props){
    super(props)
    this.classes = useStyles();
    this.theme = useTheme();
    this.user = props.user
    this.firebase = props.firebase
    this.state = {
      data: {},
      selectedPage: null
    }
  }

  loadData(){
    let dashboard = this;
    this.firebase.get_user_data(this.user, function(data){
      dashboard.setState({data: data})
    })
  }

  setSelectedPage(page){
    this.setState({'selectedPage': page})
  }

  componentDidMount(){
    this.loadData()
  }

  render(){
    return (
      <div>
        <SideMenu
          newProject={<ProjectBuilder firebase={this.firebase} user={this.user} />}
          allProjects={<AllProjects user={this.user} data={this.state.data} firebase={this.firebase}/>}
          settings={5}
          firebase={this.firebase}
          selectedPage={this.setSelectedPage.bind(this)}
        />
        <main className={this.classes.content}>
          {this.state.selectedPage}
        </main>
      </div>
    );
  }
}
