import React, { useEffect, useState, useRef } from 'react';
import {useRouteMatch} from 'react-router-dom'
import ImageUploader from 'react-images-upload'
import {Button} from '@material-ui/core'

export default function Project(props){

    const match = useRouteMatch()
    const data = props.data[match.params.id]
    const [image, setImage] = useState(null)
    const [results, setResults] = useState({})

    function onDrop(uploadedImages, url){
        setImage(uploadedImages[0])
    }

    function performPrediction(){
        const url = 'http://127.0.0.1:8080/predict/'+match.params.id
        const formData = new FormData()
        formData.append('image', image)
        const options = {
            method: 'POST',
            body: formData
        }
        fetch(url, options)
        .then(response => response.json())
        .then(result => {
            console.log(result)
        })
        .catch(error => {
            console.log(error)
        })
    }

    const ShowPredictor = () => {

        return (
            image &&
            <div>
                <Button variant='contained' color='primary' onClick={performPrediction}>Predict</Button>
            </div>
        )
    }


    return (
        <div>
            {console.log("CAME IN CHILD")}
            <h2>{data.name}</h2>
            <h5>Predict</h5>
            <ImageUploader 
                withIcon={true}
                buttonText='Upload Image'
                onChange={onDrop}
                imgExtension={['.jpg', '.jpeg', '.png']}
                singleImage={true}
                withPreview={true}
            />
            <ShowPredictor />
        </div>
    )
}
