import React, { useEffect, useState, Component } from 'react';
import ProjectBuilder from './projectBuilder'
import SideMenu from './sideMenu'
import { Switch, Route, Redirect, useRouteMatch } from "react-router-dom";
import ProjectsTable from './projectsTable';

export default class Dashboard extends Component {

  constructor(props) {

    super(props)
    this.state = {
      user: props.user,
      firebase: props.firebase,
      jobData: {}
    }
    this.match = useRouteMatch()
  }

  componentDidMount(){
    this.loadData()
  }

  loadData() {
    const self = this
    this.state.firebase.listenForData(this.state.user, function (job, job_data) {
      var newData = self.state.jobData
      newData[job] = job_data
      self.setState({jobData: newData})
    })
  }

  render(){
    return(
      <div>
        { console.log(this.state.jobData) }
        < SideMenu
          firebase = { this.state.firebase }
        />
        <Switch>
          <Route path={`${this.match.path}/new_project`}>
            <ProjectBuilder user={this.state.user} firebase={this.state.firebase} />
          </Route>
          <Route path={`${this.match.path}/all_projects`}>
            <ProjectsTable user={this.state.user} data={this.state.jobData} firebase={this.state.firebase} />
          </Route>
          <Route path={`${this.match.path}/settings`}>
            <div>
              Settings page
                </div>
          </Route>
        </Switch>
      </div >
    )
  }
}
