import React, { useEffect, useState, useRef } from 'react';
import {useRouteMatch} from 'react-router-dom'
import ImageUploader from 'react-images-upload'
import {Button} from '@material-ui/core'

export default function Project(props){

    const match = useRouteMatch()
    const data = props.data[match.params.id]
    const [image, setImage] = useState(null)

    function onDrop(image, url){
        setImage(image)
    }

    function performPrediction(){
        const url = 'https://predictor.astrum.ai/predict/'+match.params.id
        const options = {
            'method': 'POST',
            'body': {
                'image': image
            }
        }
        fetch(url, )
    }

    const ShowPredictor = () => {

        return (
            image &&
            <div>
                <Button variant='contained' color='primary' onClick={performPrediction}>Predict</Button>
            </div>
        )
    }


    return (
        <div>
            {console.log("CAME IN CHILD")}
            <h2>{data.name}</h2>
            <h5>Predict</h5>
            <ImageUploader 
                withIcon={true}
                buttonText='Upload Image'
                onChange={onDrop}
                imgExtension={['.jpg', '.jpeg', '.png']}
                singleImage={true}
                withPreview={true}
            />
            <ShowPredictor />
        </div>
    )
}
